import React from 'react';
import './Productdetail.css';

function Product_detail() {
  
    return (     
    <>

    <h2>Product Details</h2>
        <body>
    <div class="container">
    <div class="image-grid">
        <img src="logo.svg" alt="Product Image 1"></img>
        <img src="logo.svg" alt="Product Image 2"></img>
        <img src="logo.svg" alt="Product Image 3"></img>
        <img src="logo.svg" alt="Product Image 4"></img>
      </div>
      <div class="product-details">
        <h1>Product Title</h1>
        <p>Product Description</p>
        <div class="ratings">
          <span class="fa fa-star checked"></span>
          <span class="fa fa-star checked"></span>
          <span class="fa fa-star checked"></span>
          <span class="fa fa-star"></span>
          <span class="fa fa-star"></span>
          <span>3.0 (200 ratings)</span>
        </div>
        <div class="actions">
         
          <button class="comment-button">Comment</button>
          <button class="description-button">Description</button>
        </div>
        <div class="comments-container">
  <p>Comment 1</p>
  <p>Comment 2</p>
  <p>Comment 3</p>
</div>

<div class="descriptions-container">
  <p>Description 1</p>
  <p>Description 2</p>
  <p>Description 3</p>
</div>
        <div class="price">
          <p>$99.99</p>
        </div>
        <div class="colors">
          <p>Available in:</p>
          <div class="color-options">
            <div class="color-box" ></div>
            <div class="color-box" ></div>
            <div class="color-box" ></div>
          </div>
        </div>
        <div class="buttons">
          
          <button class="buy-now-button">Buy Now</button>
        </div>
      </div>
    </div>
  </body>
  

  
    </>  
    );
  }
  const commentButton = document.querySelector(".comment-button");
const descriptionButton = document.querySelector(".description-button");
const commentsContainer = document.querySelector(".comments-container");
const descriptionsContainer = document.querySelector(".descriptions-container");

commentButton.addEventListener("click", function() {
  commentsContainer.style.display = "block";
  descriptionsContainer.style.display = "none";
});

descriptionButton.addEventListener("click", function() {
  commentsContainer.style.display = "none";
  descriptionsContainer.style.display = "block";
});

  export default Product_detail;
